      "Í∞ïÎèôÍµ¨",
      "ÏÑ±Î∂ÅÍµ¨",
      "ÎèÑÎ¥âÍµ¨",
      "ÎÖ∏ÏõêÍµ¨",
      "Í∞ïÏÑúÍµ¨",
      "ÏñëÏ≤úÍµ¨",
      "Íµ¨Î°úÍµ¨",
      "Í∏àÏ≤úÍµ¨",
      "ÏÜ°ÌååÍµ¨",
      "Í∞ïÎÇ®Íµ¨",
    ],
    "Í≤ΩÍ∏∞ÎèÑ": ["ÌôîÏÑ±Ïãú ÏôÄÏö∞Î¶¨", "ÏàòÏõêÏãú ÌåîÎã¨Íµ¨", "ÏàòÏõêÏãú Ïû•ÏïàÍµ¨", "ÏàòÏõêÏãú"],
  };

  // ÏúÑÏπò Ï¢åÌëú Îç∞Ïù¥ÌÑ∞
  const LOCATION_COORDS = {
    // --- ÏÑúÏö∏ ---
    "ÏÑúÏö∏ÌäπÎ≥ÑÏãú ÏùÄÌèâÍµ¨": { lat: 37.6027, lng: 126.9292 },
    "ÏÑúÏö∏ÌäπÎ≥ÑÏãú ÏòÅÎì±Ìè¨Íµ¨": { lat: 37.5263, lng: 126.8962 },
    "ÏÑúÏö∏ÌäπÎ≥ÑÏãú Ïö©ÏÇ∞Íµ¨": { lat: 37.5311, lng: 126.9819 },
    "ÏÑúÏö∏ÌäπÎ≥ÑÏãú ÎèôÎåÄÎ¨∏Íµ¨": { lat: 37.5744, lng: 127.0396 },
    "ÏÑúÏö∏ÌäπÎ≥ÑÏãú ÎèôÏûëÍµ¨": { lat: 37.5124, lng: 126.9398 },
    "ÏÑúÏö∏ÌäπÎ≥ÑÏãú Í¥ëÏßÑÍµ¨": { lat: 37.5384, lng: 127.0822 },
    "ÏÑúÏö∏ÌäπÎ≥ÑÏãú ÎßàÌè¨Íµ¨": { lat: 37.5609, lng: 126.9084 },
    "ÏÑúÏö∏ÌäπÎ≥ÑÏãú ÏÑúÏ¥àÍµ¨": { lat: 37.4836, lng: 127.0327 },
    "ÏÑúÏö∏ÌäπÎ≥ÑÏãú Í∞ïÎèôÍµ¨": { lat: 37.5301, lng: 127.1238 },
    "ÏÑúÏö∏ÌäπÎ≥ÑÏãú ÏÑ±Î∂ÅÍµ¨": { lat: 37.5894, lng: 127.0167 },
    "ÏÑúÏö∏ÌäπÎ≥ÑÏãú ÎèÑÎ¥âÍµ¨": { lat: 37.6688, lng: 127.0471 },
    "ÏÑúÏö∏ÌäπÎ≥ÑÏãú ÎÖ∏ÏõêÍµ¨": { lat: 37.6543, lng: 127.0565 },
    "ÏÑúÏö∏ÌäπÎ≥ÑÏãú Í∞ïÏÑúÍµ¨": { lat: 37.5509, lng: 126.849 },
    "ÏÑúÏö∏ÌäπÎ≥ÑÏãú ÏñëÏ≤úÍµ¨": { lat: 37.5169, lng: 126.8664 },
    "ÏÑúÏö∏ÌäπÎ≥ÑÏãú Íµ¨Î°úÍµ¨": { lat: 37.4954, lng: 126.8874 },
    "ÏÑúÏö∏ÌäπÎ≥ÑÏãú Í∏àÏ≤úÍµ¨": { lat: 37.4568, lng: 126.895 },
    "ÏÑúÏö∏ÌäπÎ≥ÑÏãú ÏÜ°ÌååÍµ¨": { lat: 37.5145, lng: 127.1066 },
    "ÏÑúÏö∏ÌäπÎ≥ÑÏãú Í∞ïÎÇ®Íµ¨": { lat: 37.5173, lng: 127.0473 },

    // --- Í≤ΩÍ∏∞ ---
    "Í≤ΩÍ∏∞ÎèÑ ÌôîÏÑ±Ïãú ÏôÄÏö∞Î¶¨": { lat: 37.2092, lng: 126.9769 },
    "Í≤ΩÍ∏∞ÎèÑ ÏàòÏõêÏãú ÌåîÎã¨Íµ¨": { lat: 37.292, lng: 127.0107 },
    "Í≤ΩÍ∏∞ÎèÑ ÏàòÏõêÏãú Ïû•ÏïàÍµ¨": { lat: 37.2951, lng: 126.9739 },
    "Í≤ΩÍ∏∞ÎèÑ ÏàòÏõêÏãú": { lat: 37.2636, lng: 127.0286 },
  };

  // URL ÏøºÎ¶¨ (Ï±óÎ¥áÏóêÏÑú "ÏßÄÎèÑÎ°ú Ïù¥Îèô"Ìï† Îïå lat/lng Î∂ôÏó¨Ï£ºÎäî Ïö©ÎèÑ)
  const [searchParams] = useSearchParams();
  const urlLat = searchParams.get("lat");
  const urlLng = searchParams.get("lng");

  // URLÏóê lat, lngÍ∞Ä ÏûàÏúºÎ©¥ 'all' Î™®Îìú, ÏóÜÏúºÎ©¥ Í∏∞Î≥∏Í∞í(ÏÑúÏö∏ÌäπÎ≥ÑÏãú)
  const initialProvince = urlLat ? "all" : PROVINCES[0];
  const initialDistrict = urlLat ? "all" : DISTRICTS_BY_PROVINCE[PROVINCES[0]][0];

  const [selectedProvince, setSelectedProvince] = useState(initialProvince);
  const [selectedDistrict, setSelectedDistrict] = useState(initialDistrict);

  // === ÏßÄÎèÑ Î∞è ÌïÑÌÑ∞ Í¥ÄÎ†® ÏÉÅÌÉú ===
  const [mapInstance, setMapInstance] = useState(null);
  const [markers, setMarkers] = useState([]);
  const [infowindow, setInfowindow] = useState(null);
  const [activeFilter, setActiveFilter] = useState("Ï†ÑÏ≤¥");

  const [restaurants, setRestaurants] = useState([]); // üî• Î∞±ÏóîÎìúÏóêÏÑú Î∞õÏïÑÏò® ÏãùÎãπ Î¶¨Ïä§Ìä∏
  const foodCategories = ["Ï†ÑÏ≤¥", "ÌïúÏãù", "Ï§ëÏãù", "ÏùºÏãù", "ÏñëÏãù", "Ïπ¥Ìéò"];

  // Ïãú/ÎèÑ Î≥ÄÍ≤Ω
  const handleProvinceChange = (e) => {
    const newProvince = e.target.value;
    setSelectedProvince(newProvince);

    if (newProvince === "all") {
      setSelectedDistrict("all");
    } else {
      const newDistricts = DISTRICTS_BY_PROVINCE[newProvince];
      if (newDistricts && newDistricts.length > 0) {
        setSelectedDistrict(newDistricts[0]);
      }
    }
  };

  // ÏãùÎãπ Î¶¨Ïä§Ìä∏ ÌÅ¥Î¶≠ Ïãú ÏßÄÎèÑ Ïù¥Îèô + Ï†ïÎ≥¥Ï∞Ω
  const handleRestaurantClick = (restaurant) => {
    if (!mapInstance || !infowindow) return;

    const { lat, lng } = restaurant;
    const moveLatLng = new window.kakao.maps.LatLng(lat, lng);
    mapInstance.panTo(moveLatLng);

    const targetMarker = markers.find(
      (marker) =>
        marker.getPosition().getLat() === lat &&
        marker.getPosition().getLng() === lng
    );

    if (!targetMarker) return;

    const content = `
      <div style="padding:15px; width:280px; font-family: 'Malgun Gothic', sans-serif;">
        <h4 style="margin:0 0 8px 0; font-size:16px;">${restaurant.res_name}</h4>
        <p style="font-size:12px; margin:0 0 4px 0; color:#666;">
          <strong>Ïπ¥ÌÖåÍ≥†Î¶¨:</strong> ${restaurant.category || "-"}
        </p>
        <p style="font-size:12px; margin:0 0 4px 0; color:#666;">
          <strong>Ï£ºÏÜå:</strong> ${restaurant.address || "-"}
        </p>
        <p style="font-size:12px; margin:0 0 4px 0; color:#666;">
          <strong>ÌèâÏ†ê:</strong> ${restaurant.score ?? "-"}
        </p>
      </div>
    `;
    infowindow.setContent(content);
    infowindow.open(mapInstance, targetMarker);
  };

  // 1) Ïπ¥Ïπ¥Ïò§Îßµ Ïä§ÌÅ¨Î¶ΩÌä∏ Î°úÎìú + ÏßÄÎèÑ Í∞ùÏ≤¥ 1Î≤àÎßå ÏÉùÏÑ±
  useEffect(() => {
    const initMap = () => {
      const center = new window.kakao.maps.LatLng(37.5665, 126.978); // Í∏∞Î≥∏ ÏÑúÏö∏
      const options = { center, level: 4 };
      const map = new window.kakao.maps.Map(mapContainer.current, options);
      const iw = new window.kakao.maps.InfoWindow({
        removable: true,
        zIndex: 1,
      });

      setMapInstance(map);
      setInfowindow(iw);
    };

    if (window.kakao && window.kakao.maps) {
      window.kakao.maps.load(initMap);
    } else {
      const script = document.createElement("script");
      script.src =
        "//dapi.kakao.com/v2/maps/sdk.js?appkey=920ae06c68357b930c999434271d8194&autoload=false";
      script.async = true;
      script.onload = () => {
        window.kakao.maps.load(initMap);
      };
      document.head.appendChild(script);
    }
  }, []);

  // 2) ÏßÄÎèÑ Ï§ÄÎπÑÎêòÎ©¥ Î∞±ÏóîÎìúÏóêÏÑú ÎßàÏª§ Îç∞Ïù¥ÌÑ∞ Î∞õÏïÑÏò§Í∏∞
  useEffect(() => {
    if (!mapInstance) return;

    async function loadData() {
      try {
        // üî• URLÏóê lat/lng ÏûàÏúºÎ©¥ ÏÑ∏ÏÖòÏóê ÌòÑÏû¨ ÏúÑÏπò Ï†ÄÏû• (radius Í≤ÄÏÉâ ÎåÄÎπÑ)
        if (urlLat && urlLng) {
          await apiPost("/location", {
            lat: parseFloat(urlLat),
            lng: parseFloat(urlLng),
          });
        }

        const data = await apiGet("/restaurants/markers");
        // data: [{res_id, res_name, lat, lng, address, category, score}, ...]
        setRestaurants(data || []);

        // Ïπ¥Ïπ¥Ïò§ ÎßàÏª§ ÏÉùÏÑ±
        const createdMarkers = (data || []).map((resto) => {
          const markerPosition = new window.kakao.maps.LatLng(
            resto.lat,
            resto.lng
          );
          const marker = new window.kakao.maps.Marker({ position: markerPosition });
          marker.category = resto.category || "Í∏∞ÌÉÄ";
          marker.setMap(mapInstance);

          // ÎßàÏª§ ÌÅ¥Î¶≠ Ïãú Ï†ïÎ≥¥Ï∞Ω Ïò§Ìîà
          window.kakao.maps.event.addListener(marker, "click", () => {
            if (!infowindow) return;

            const content = `
              <div style="padding:15px; width:280px; font-family: 'Malgun Gothic', sans-serif;">
                <h4 style="margin:0 0 8px 0; font-size:16px;">${resto.res_name}</h4>
                <p style="font-size:12px; margin:0 0 4px 0; color:#666;">
                  <strong>Ïπ¥ÌÖåÍ≥†Î¶¨:</strong> ${resto.category || "-"}
                </p>
                <p style="font-size:12px; margin:0 0 4px 0; color:#666;">
                  <strong>Ï£ºÏÜå:</strong> ${resto.address || "-"}
                </p>
                <p style="font-size:12px; margin:0 0 4px 0; color:#666;">
                  <strong>ÌèâÏ†ê:</strong> ${resto.score ?? "-"}
                </p>
              </div>
            `;
            infowindow.setContent(content);
            infowindow.open(mapInstance, marker);
          });

          return marker;
        });

        setMarkers(createdMarkers);
      } catch (err) {
        console.error("ÏßÄÎèÑ/ÎßàÏª§ Î°úÎìú Ïã§Ìå®:", err);
      }
    }

    loadData();
  }, [mapInstance, urlLat, urlLng, infowindow]);

  // 3) Ïãú/ÎèÑ, Íµ¨ Î≥ÄÍ≤Ω or URL Ï¢åÌëúÏóê Îî∞Îùº ÏßÄÎèÑ Ï§ëÏã¨Îßå Ïù¥Îèô
  useEffect(() => {
    if (!mapInstance) return;

    let targetCoords;

    if (selectedProvince === "all" && urlLat && urlLng) {
      targetCoords = {
        lat: parseFloat(urlLat),
        lng: parseFloat(urlLng),
      };
    } else {
      const currentKey = `${selectedProvince} ${selectedDistrict}`;
      targetCoords =
        LOCATION_COORDS[currentKey] || LOCATION_COORDS["ÏÑúÏö∏ÌäπÎ≥ÑÏãú ÏùÄÌèâÍµ¨"];
    }

    const center = new window.kakao.maps.LatLng(
      targetCoords.lat,
      targetCoords.lng
    );
    mapInstance.setCenter(center);
  }, [mapInstance, selectedProvince, selectedDistrict, urlLat, urlLng]);

  // 4) ÌïÑÌÑ∞ Î≥ÄÍ≤Ω Ïãú ÎßàÏª§ ÌëúÏãú/Ïà®ÍπÄ
  useEffect(() => {
    if (!mapInstance || markers.length === 0) return;

    markers.forEach((marker) => {
      if (activeFilter === "Ï†ÑÏ≤¥" || marker.category === activeFilter) {
        marker.setMap(mapInstance);
      } else {
        marker.setMap(null);
      }
    });
  }, [activeFilter, markers, mapInstance]);

  // ÌòÑÏû¨ ÌïÑÌÑ∞Ïóê ÎßûÎäî ÏãùÎãπ Î™©Î°ù
  const filteredRestaurants =
    activeFilter === "Ï†ÑÏ≤¥"
      ? restaurants
      : restaurants.filter((r) => r.category === activeFilter);

  return (
    <div style={{ display: "flex", height: "100vh" }}>
      {/* ÏôºÏ™Ω: ÏßÄÎèÑ ÏòÅÏó≠ */}
      <div className="Map" style={{ width: "60%", padding: "20px" }}>
        <div
          style={{ display: "flex", alignItems: "center", marginBottom: "20px" }}
        >
          <button
            onClick={() => navigate(-1)}
            style={{
              padding: "8px 12px",
              marginRight: "16px",
              border: "1px solid #ddd",
              borderRadius: "4px",
              backgroundColor: "#fff",
              cursor: "pointer",
            }}
          >
            Îí§Î°úÍ∞ÄÍ∏∞
          </button>
          <h1 style={{ marginRight: "20px", fontSize: "1.5em" }}>ÌòÑÏû¨ ÏúÑÏπò:</h1>

          {/* Ïãú/ÎèÑ ÏΩ§Î≥¥Î∞ïÏä§ */}
          <select
            value={selectedProvince}
            onChange={handleProvinceChange}
            style={{
              padding: "8px",
              marginRight: "10px",
              color: selectedProvince === "all" ? "#999" : "#000",
            }}
          >
            <option value="all">--Ï†ÑÏ≤¥--</option>
            {PROVINCES.map((province) => (
              <option key={province} value={province}>
                {province}
              </option>
            ))}
          </select>

          {/* Ïãú/Íµ∞/Íµ¨ ÏΩ§Î≥¥Î∞ïÏä§ */}
          <select
            value={selectedDistrict}
            onChange={(e) => setSelectedDistrict(e.target.value)}
            style={{
              padding: "8px",
              color: selectedDistrict === "all" ? "#999" : "#000",
            }}
            disabled={selectedProvince === "all"}
          >
            {selectedProvince === "all" ? (
              <option value="all">--Ï†ÑÏ≤¥--</option>
            ) : (
              DISTRICTS_BY_PROVINCE[selectedProvince] &&
              DISTRICTS_BY_PROVINCE[selectedProvince].map((district) => (
                <option key={district} value={district}>
                  {district}
                </option>
              ))
            )}
          </select>
        </div>

        <div
          id="kakao-map"
          ref={mapContainer}
          style={{ width: "100%", height: "calc(100% - 70px)" }}
        />
      </div>

      {/* Ïò§Î•∏Ï™Ω: ÌïÑÌÑ∞ + Î¶¨Ïä§Ìä∏ */}
      <div
        className="Sidebar"
        style={{ width: "40%", padding: "20px", borderLeft: "1px solid #eee" }}
      >
        {/* Í≤ÄÏÉâ ÌïÑÌÑ∞ */}
        <div className="FilterContainer" style={{ marginBottom: "20px" }}>
          {foodCategories.map((category) => (
            <button
              key={category}
              onClick={() => setActiveFilter(category)}
              style={{
                padding: "8px 16px",
                marginRight: "8px",
                border: "1px solid #ddd",
                borderRadius: "16px",
                cursor: "pointer",
                backgroundColor:
                  activeFilter === category ? "#2c7a7b" : "#fff",
                color: activeFilter === category ? "#fff" : "#000",
              }}
            >
              {category}
            </button>
          ))}
        </div>

        {/* ÏãùÎãπ Î¶¨Ïä§Ìä∏ */}
        <div className="RestaurantList">
          {filteredRestaurants.map((restaurant) => (
            <div
              key={restaurant.res_id}
              onClick={() => handleRestaurantClick(restaurant)}
              style={{
                padding: "15px",
                borderBottom: "1px solid #f0f0f0",
                cursor: "pointer",
              }}
            >
              <h3 style={{ margin: 0, fontSize: "1.1em" }}>
                {restaurant.res_name}
              </h3>
              <p style={{ margin: "5px 0 0", color: "#888" }}>
                {restaurant.category}
              </p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Map;
