// src/login.js
import React, { useState } from "react";
import "./login.css";
import { login } from "./authApi";
import { useNavigate } from "react-router-dom";

function Login() {
  const navigate = useNavigate();

  const [userId, setUserId] = useState("");
  const [password, setPassword] = useState("");
  const [loading, setLoading] = useState(false);

  const handleLogin = async () => {
    if (!userId || !password) {
      alert("ÏïÑÏù¥ÎîîÏôÄ ÎπÑÎ∞ÄÎ≤àÌò∏Î•º Î™®Îëê ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.");
      return;
    }

    try {
      setLoading(true);
      const res = await login(userId, password);
      alert(res.message || "Î°úÍ∑∏Ïù∏ ÏÑ±Í≥µ!");

      // Î°úÍ∑∏Ïù∏ ÏÑ±Í≥µ ÌõÑ ÎÇ¥ Ï†ïÎ≥¥ ÌéòÏù¥ÏßÄÎ°ú Ïù¥Îèô
      navigate("/me");
    } catch (err) {
      console.error(err);
      alert(err.message || "Î°úÍ∑∏Ïù∏Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="auth-wrapper">
      <div className="auth-card">
        {/* üîô Îí§Î°ú Í∞ÄÍ∏∞ Î≤ÑÌäº */}
        <button
          type="button"
          className="back-btn"
          onClick={() => navigate("/")}
        >
          ‚Üê Î©îÏù∏ÏúºÎ°ú
        </button>

        <h1 className="auth-title">Î°úÍ∑∏Ïù∏</h1>
        <p className="auth-subtitle">ÏïÑÏù¥ÎîîÏôÄ ÎπÑÎ∞ÄÎ≤àÌò∏Î•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî</p>

        <div className="form-group">
          <label className="form-label">ÏïÑÏù¥Îîî</label>
          <div className="input-wrapper">
            <span className="input-icon">üîñ</span>
            <input
              className="auth-input"
              placeholder="user_id"
              value={userId}
              onChange={(e) => setUserId(e.target.value)}
            />
          </div>
        </div>

        <div className="form-group">
          <label className="form-label">ÎπÑÎ∞ÄÎ≤àÌò∏</label>
          <div className="input-wrapper">
            <span className="input-icon">üîí</span>
            <input
              type="password"
              className="auth-input"
              placeholder="ÎπÑÎ∞ÄÎ≤àÌò∏"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </div>
        </div>

        <button
          type="button"
          className="submit-btn"
          onClick={handleLogin}
          disabled={loading}
        >
          {loading ? "Î°úÍ∑∏Ïù∏ Ï§ë..." : "Î°úÍ∑∏Ïù∏"}
        </button>
      </div>
    </div>
  );
}

export default Login;
